import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { Button } from './components/minimal/button';
import { Badge } from './components/minimal/badge';
import { Heart, MessageCircle } from 'lucide-react';

interface Comment {
  id: string;
  text: string;
  author: string;
  createdAt: Date;
}

interface Profile {
  id: string;
  name: string;
  age: number;
  title: string;
  location: string;
  description: string;
  image?: string;
  interests: string[];
  createdAt: Date;
  comments: Comment[];
}

