function AddPostForm({ onAddPost, isOpen, setIsOpen }: { onAddPost: (profile: Omit<Profile, 'id' | 'createdAt'>) => void; isOpen: boolean; setIsOpen: (open: boolean) => void }) {
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    title: '',
    location: 'San Francisco, CA',
    description: '',
    interests: ''
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (formData.name && formData.age && formData.title && formData.description) {
      const newProfile: Omit<Profile, 'id' | 'createdAt'> = {
        name: formData.name,
        age: parseInt(formData.age),
        title: formData.title,
        location: formData.location,
        description: formData.description,
        interests: formData.interests.split(',').map(i => i.trim()).filter(i => i.length > 0),
        comments: []
      };
      onAddPost(newProfile);
      setFormData({
        name: '',
        age: '',
        title: '',
        location: 'San Francisco, CA',
        description: '',
        interests: ''
      });
      setIsOpen(false);
    }
  };

  if (!isOpen) {
    return null;
  }

  return (
    <>
      {/* Backdrop */}
      <div 
        className="fixed inset-0 bg-black bg-opacity-50 z-40"
        onClick={() => setIsOpen(false)}
